using UnityEngine; using System.Collections; public class barriors : MonoBehaviour { 	public float force = 2000.0f; 	public float forceRadius = 1.0f; 	void OnCollisionEnter() 	{ 		foreach(Collider col in Physics.OverlapSphere(transform.position,forceRadius)) 		{ 			if(col.gameObject.GetComponent<Rigidbody>()) 			{ 				col.gameObject.GetComponent<Rigidbody>().AddExplosionForce(force,transform.position,forceRadius); 			} 		} 	} }